import { MongoClient, ObjectID } from 'mongodb';
import users from "./model";
import mongoose, { ConnectionStates } from "mongoose";
mongoose.set('useFindAndModify', false);

const url = 'mongodb://localhost:27017';
const dbName = 'taskDB';
mongoose.connect(url + '/' + dbName);

const Controller = {
    main: function (req, res) {
        res.render("main.ejs")
    },

    regPage: function (req, res) {
        res.render("registration.ejs");
    },

    logInPage: function (req, res) {
        res.render("logIn.ejs")
    },

    waiting: function (req, res) {
        res.send("<h1>You have no role still!</h1>");
    },

    get: function (request, response) {
        users.find()
            .then(inf => {
                response.send(inf);
            })
            .catch(
                error => {
                    response.status(500).send(error);
                }
            );
    },

    teacherPage: function (req, res) {
        let cookie = req.cookies;
        if (cookie && cookie.role == "teacher") {
            return res.render("teacherPage.ejs");
        }
        return res.send("You are not logged in!");

    },

    editAssessmentsPage: function (req, res) {
        let item = req.body;
        let grade = parseInt(item.grade);
        users.find({ grade: grade }).then(inf => {
            if (!grade) return res.status(400).send("You did not enter a grade!");
            if (typeof (grade) != "number") return res.status(400).send("A grade must be a number!");
            if ((grade ^ 0) !== grade) return res.status(400).send("A grade must be integer!");
            if (grade < 1 || grade > 11) return res.status(400).send("A grade must be between 1 and 11!");
            return res.render("assessmentsPage.ejs", { data: { data: inf, subject: item.subject } });
        });
    },

    adminPage: function (req, res) {
        let cookie = req.cookies;
        if (cookie && cookie.role == "admin") {
            return res.render("admin.ejs");
        }
        return res.send("You are not logged in!");
    },

    editRoles: function (req, res) {
        let item = req.body;
        users.find().then(db => {
            let stud = [];
            let teach = [];
            if (item.students) {
                if (typeof (item.students) == "string") {
                    item.students = [item.students];
                }
                for (let student of item.students) {
                    users.findOneAndUpdate({ email: student }, { role: "student" }, { new: true }, function (error, user) {
                        if (error) return res.send(error);
                        if (user) stud.push(user.email);
                    });
                }
            }
            if (item.teachers) {
                if (typeof (item.teachers) == "string") {
                    item.teachers = [item.teachers];
                }
                for (let teacher of item.teachers) {
                    users.findOneAndUpdate({ email: teacher }, { role: "teacher" }, { new: true }, function (error, user) {
                        if (error) return res.send(error);
                        if (user) teach.push(user.email);
                    });
                }
            }
            setTimeout(() => {
                return res.render("changedUsers.ejs", { students: stud, teachers: teach });
            }, 100)
        });
    },

    addSubjects: function (req, res) {
        let item = req.body;
        let grade = parseInt(item.grade);
        if (!grade) return res.status(400).send("You did not enter a grade!");
        if (typeof (grade) != "number") return res.status(400).send("A grade must be a number!");
        if ((grade ^ 0) !== grade) return res.status(400).send("A grade must be integer!");
        if (grade < 1 || grade > 11) return res.status(400).send("A grade must be between 1 and 11!");
        if (!item.subjects) return res.status(400).send("You did not enter subjects!");
        if (typeof (item.subjects) == "string") item.subjects = [item.subjects];
        users.find({ grade: grade }).then(inf => {
            for (let student of inf) {
                for (let s of item.subjects) {
                    if (!student.subjects.hasOwnProperty(s))
                        student.subjects[`${s}`] = { topic1: 0 };
                }
                const updatedUser = { subjects: student.subjects };
                users.findOneAndUpdate({ email: student.email }, updatedUser, { new: true }, function (error, user) {
                    if (error) return res.send(error)
                })
            }
            return res.send(inf)
        })
    },

    removeSubjects: function (req, res) {
        let item = req.body;
        let grade = parseInt(item.grade);
        if (!grade) return res.status(400).send("You did not enter a grade!");
