import { MongoClient, ObjectID } from 'mongodb';
import users from "./model";
import mongoose, { ConnectionStates } from "mongoose";
mongoose.set('useFindAndModify', false);

const url = 'mongodb://localhost:27017';
const dbName = 'taskDB';
mongoose.connect(url + '/' + dbName);

const regController = {
    registration: function (request, response) {
        const newItem = new users(request.body);
        let check = true;
        users.find().then((inf) => {
            for (let i = 0; i < inf.length; i++) {
                if (newItem.email == inf[i].email) { check = false; break; }
            }
            if (!newItem.email) return response.status(401).send("You did not enter an email!");
            if (!newItem.password) return response.status(401).send("You did not enter a password!");
            let grade = newItem.grade;
            if (grade) {
                if (typeof (grade) != "number") return response.status(401).send("A grade must be a number!");
                if ((grade ^ 0) !== grade) return response.status(401).send("A grade must be integer!");
                if (grade > 11 || grade < 1) return response.status(401).send("A grade must be between 1 and 12!");
            }
            if (!newItem.fullName) return response.status(401).send("You did not enter your name!");
            if (inf.length == 0) newItem.role = "admin";
            if (check)
                newItem.save().then(user => {
                    return response.render("waitForRole.ejs")
                })
            else return response.status(401).send("This email has already been taken!!!")
        });
    },

    logIn: function (req, res) {
        let item = req.body;
        if (!item.email) return res.status(400).send("You did not enter an email!");
        if (!item.password) return res.status(400).send("You did not enter a password!");
        users.findOne({ email: item.email, password: item.password }).then(inf => {
            if (!inf) return res.status(400).send("Check your password or email!!!");
            if (inf.role == "") return res.render("waitForRole.ejs");
            if (inf.role == "student") {
                res.cookie("role", "student");
                return res.render("studentSubjects.ejs", { data: inf.subjects });
            }
            if (inf.role == "teacher") {
                res.cookie("role", "teacher");
                return res.redirect("/teacherPage");
            }
            if (inf.role == "admin") {
                res.cookie("role", "admin");
                return res.redirect("/adminPage");
            }
            else return res.send(inf)
        });

    },
}

export default regController;

