import Router from "express";
import Controller from "./controller";
import regController from "./regController";

const studRouter = new Router();

studRouter.get("/test", Controller.test);
studRouter.post("/test1", Controller.test1);

//render
studRouter.get("/main", Controller.main);
studRouter.get("/registration", Controller.regPage);
studRouter.get("/loginPage", Controller.logInPage);
studRouter.get("/waitForRole", Controller.waiting);
studRouter.get("/teacherPage", Controller.teacherPage);
studRouter.get("/adminPage", Controller.adminPage);

//registration
studRouter.post("/login", regController.logIn);
studRouter.post("/add", regController.registration);

//other functions
studRouter.post("/editRoles", Controller.editRoles);
studRouter.post("/addSubjects", Controller.addSubjects);
studRouter.post("/removeSubjects", Controller.removeSubjects);
studRouter.post("/addTopics", Controller.addTopics);
studRouter.post("/removeTopics", Controller.removeTopics);
studRouter.post("/editAssessmentsPage", Controller.editAssessmentsPage);

studRouter.get("/gett", Controller.get);
studRouter.delete("/delete", Controller.deleteById);

export default studRouter;

